.nav{
    background: #111;
    color: #777;
    font-family: sans-serif;
    display: flex;
    justify-content: space-between;
    padding-left: 30px;
    padding: 30px;
    padding-top: 10px;
    padding-bottom: 10px;
}

.nav .logo{
    text-transform: uppercase;
    text-decoration: none;
    font-size: 25px;
    color: #fa0;
    font-weight: 700;
    padding-top: 5px;
}

.nav .logo span{
    color: white;
}


.nav .logo:hover{
    color: #ff4000;
}

.nav .nav-menu ul{
    display: inline-flex;
    list-style-type: none;
    margin: 0;
    padding: 0;
}

.nav .nav-menu li{
    padding-left: 15px;
    padding-top: 8px;
}

.nav .nav-menu li a{
    text-decoration: none;
    text-transform: uppercase;
    font-size: 14px;
    color: #fa0;
    font-weight: 600;
}

.nav .nav-menu li a span{
    font-size: 14px;
    color: #fa0;
    font-weight: 600;
    margin-right: 5px;
}


nav .nav-menu li a:hover{
    color:#ff4000;
}

.nav .nav-search{
    margin-top: 0;
    flex: 1;
    display: flex;
    margin-left: 15%;
    margin-right: 15%;
}

.nav .nav-search input{
    flex: 1;
    color:#555;
    border: 1px solid #ff4000;
    height: 35px;
    border-right: 0px;
    padding-left: 10px;
}

.nav .nav-search button{
    color:#fff;
    background: #fa0;
    border: 1px solid #c80;
    height: 35px;
    border-left: 0px;
    width: 200px;
    font-size: 18px;
    text-transform: uppercase;
    font-weight: 600;
}

.nav .nav-search button:hover{
    background: #ff4000;
}
